import React, { useState } from "react";
import "./components5.css";

// Импортируйте ваши фото здесь, например:
// import australianGP from './images/australian-gp.jpg';
// import bahrainGP from './images/bahrain-gp.jpg';
// и т.д.

const Tracci = () => {
  const [showAll, setShowAll] = useState(false);

  const tracks = [
    {
      id: 1,
      name: "AUSTRALIAN GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      // ВСТАВЬТЕ ФОТО ЗДЕСЬ: импортируйте фото выше и укажите здесь
      image: null, // Замените null на импортированное фото, например: australianGP
    },
    {
      id: 2,
      name: "BAHRAIN GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото, например: bahrainGP
    },
    {
      id: 3,
      name: "CHINESE GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 4,
      name: "SAUDI ARABIAN GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 5,
      name: "JAPANESE GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 6,
      name: "SPANISH GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 7,
      name: "MONACO GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 8,
      name: "CANADIAN GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 9,
      name: "BRITISH GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
    {
      id: 10,
      name: "ITALIAN GRAND PRIX",
      driver: "MORG DOTTIMUS >",
      image: null, // Замените null на импортированное фото
    },
  ];

  const displayedTracks = showAll ? tracks : tracks.slice(0, 5);

  return (
    <div className="tracci-container">
      <h1 className="tracci-title">TPACCbl</h1>
      <div className="tracks-grid">
        {displayedTracks.map((track) => (
          <div key={track.id} className="track-card">
            {/* МЕСТО ДЛЯ ФОТО - ВСТАВЬТЕ ВАШЕ ФОТО ЗДЕСЬ */}
            <div className="track-image">
              {track.image ? (
                // Если фото добавлено - отображаем его
                <img
                  src={track.image}
                  alt={track.name}
                  className="track-photo"
                />
              ) : (
                // Если фото не добавлено - заглушка
                <div className="track-image-placeholder">
                  <span>Добавьте фото трассы</span>
                </div>
              )}
            </div>
            <div className="track-info">
              <h3 className="track-name">{track.name}</h3>
              <p className="track-driver">{track.driver}</p>
            </div>
          </div>
        ))}
      </div>
      {!showAll && (
        <button className="show-all-button" onClick={() => setShowAll(true)}>
          показать все трассы
        </button>
      )}
    </div>
  );
};

export default Tracci;
