import React from "react";
import '../../components2.css';

const HeadScuderri = ({ isPartnersOpen, togglePartners }) => {
  // ========== СОСТОЯНИЯ КОМПОНЕНТА ==========

  // Состояние для модального окна формы
  const [isModalOpen, setIsModalOpen] = React.useState(false);

  // Данные формы
  const [formData, setFormData] = React.useState({
    name: "",
    email: "",
    message: "",
    phone: "",
  });

  // Состояние отправки формы
  const [isSubmitting, setIsSubmitting] = React.useState(false);
  const [submitMessage, setSubmitMessage] = React.useState("");
  const [submitStatus, setSubmitStatus] = React.useState("");

  // Состояние меню для мобильных устройств
  const [isMobileMenuOpen, setIsMobileMenuOpen] = React.useState(false);

  // Ссылки на DOM элементы для анимации
  const modalRef = React.useRef(null);
  const animationRef = React.useRef(null);
  const buttonRef = React.useRef(null);

  // ========== ЭФФЕКТЫ ==========

  // Эффект для загрузки сохраненных данных из localStorage
  React.useEffect(() => {
    const savedData = localStorage.getItem("ferrariContactForm");
    if (savedData) {
      setFormData(JSON.parse(savedData));
    }
  }, []);

  // Эффект для сохранения данных формы в localStorage при изменении
  React.useEffect(() => {
    localStorage.setItem("ferrariContactForm", JSON.stringify(formData));
  }, [formData]);

  // Эффект для управления анимацией модального окна
  React.useEffect(() => {
    if (isModalOpen) {
      openModalAnimation();
    }
    return () => {
      if (animationRef.current) {
        cancelAnimationFrame(animationRef.current);
      }
    };
  }, [isModalOpen]);

  // ========== ФУНКЦИИ ОБРАБОТКИ ФОРМЫ ==========

  // Обработчик изменения полей формы
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Функция отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitMessage("");
    setSubmitStatus("");

    try {
      // Отправка данных на сервер formcarry.com
      const response = await fetch("https://formcarry.com/s/YOUR_FORM_ID", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (result.code === 200) {
        // Успешная отправка
        setSubmitStatus("success");
        setSubmitMessage("Сообщение успешно отправлено!");

        // Очистка формы
        setFormData({
          name: "",
          email: "",
          message: "",
          phone: "",
        });

        // Очистка localStorage
        localStorage.removeItem("ferrariContactForm");

        // Закрытие модального окна через 3 секунды
        setTimeout(() => {
          setIsModalOpen(false);
        }, 3000);
      } else {
        // Ошибка отправки
        setSubmitStatus("error");
        setSubmitMessage("Ошибка при отправке. Попробуйте еще раз.");
      }
    } catch (error) {
      // Ошибка сети
      setSubmitStatus("error");
      setSubmitMessage("Сетевая ошибка. Проверьте подключение к интернету.");
      console.error("Form submission error:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  // ========== ФУНКЦИИ АНИМАЦИИ ==========

  // Анимация открытия модального окна
  const openModalAnimation = () => {
    if (!modalRef.current || !buttonRef.current) return;

    const modal = modalRef.current;
    const button = buttonRef.current;
    const buttonRect = button.getBoundingClientRect();

    // Начальная позиция - место кнопки
    modal.style.opacity = "0";
    modal.style.transform = `translate(${buttonRect.left}px, ${buttonRect.top}px)`;
    modal.style.width = `${buttonRect.width}px`;
    modal.style.height = `${buttonRect.height}px`;

    let startTime = null;
    const duration = 500; // 0.5 секунды

    const animate = (timestamp) => {
      if (!startTime) startTime = timestamp;
      const progress = timestamp - startTime;
      const percentage = Math.min(progress / duration, 1);

      // Эффект easeOutCubic
      const easeOutCubic = 1 - Math.pow(1 - percentage, 3);

      // Интерполяция позиции и размера
      const currentOpacity = easeOutCubic;
      const currentTransform = `translate(0, 0)`;
      const currentWidth = `90%`;
      const currentHeight = `auto`;

      modal.style.opacity = currentOpacity;
      modal.style.transform = currentTransform;
      modal.style.width = currentWidth;
      modal.style.height = currentHeight;

      if (percentage < 1) {
        animationRef.current = requestAnimationFrame(animate);
      }
    };

    animationRef.current = requestAnimationFrame(animate);
  };

  // Функция закрытия модального окна
  const closeModal = () => {
    setIsModalOpen(false);
  };

  // ========== РЕНДЕРИНГ КОМПОНЕНТА ==========

  return (
    <div className="head-scuderri">
      {/* ========== БЛОК ФОНА И ВИДЕО ========== */}
      <div className="background-container">
        {/* Замените путь на ваше видео */}
        <video className="background-video" autoPlay muted loop playsInline>
          <source src="/videos/ferrari-background.mp4" type="video/mp4" />
          {/* Запасное изображение если видео не загрузится */}
          <img src="/images/ferrari-bg.jpg" alt="Ferrari Background" />
        </video>

        {/* Наложение для лучшей читаемости текста */}
        <div className="video-overlay"></div>
      </div>

      {/* ========== ХЕДЕР С НАВИГАЦИЕЙ ========== */}
      <header className="header">
        <div className="container">
          {/* Логотип - замените на ваш */}
          <div className="logo">
            <img
              src="/logos/ferrari-logo.png"
              alt="Scuderia Ferrari"
              className="logo-img"
            />
          </div>

          {/* Навигация для десктопа */}
          <nav className="desktop-nav">
            <ul className="nav-list">
              <li className="nav-item">ТРОФЕИ</li>
              <li className="nav-item">ИСТОРИЯ</li>
              <li className="nav-item partners-item" onClick={togglePartners}>
                ПАРТНЕРЫ
              </li>
              <li className="nav-item">ПИЛОТЫ</li>
              <li className="nav-item">КОНТАКТЫ</li>
            </ul>
          </nav>

          {/* Кнопка меню для мобильных */}
          <button
            className="mobile-menu-btn"
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          >
            <span className="menu-line"></span>
            <span className="menu-line"></span>
            <span className="menu-line"></span>
          </button>
        </div>
      </header>

      {/* ========== МОБИЛЬНОЕ МЕНЮ ========== */}
      <div className={`mobile-menu ${isMobileMenuOpen ? "open" : ""}`}>
        <div className="mobile-menu-content">
          <ul className="mobile-nav-list">
            <li className="mobile-nav-item">ТРОФЕИ</li>
            <li className="mobile-nav-item">ИСТОРИЯ</li>
            <li
              className="mobile-nav-item"
              onClick={() => {
                togglePartners();
                setIsMobileMenuOpen(false);
              }}
            >
              ПАРТНЕРЫ
            </li>
            <li className="mobile-nav-item">ПИЛОТЫ</li>
            <li className="mobile-nav-item">КОНТАКТЫ</li>
            <li
              className="mobile-nav-item contact-btn"
              onClick={() => {
                setIsModalOpen(true);
                setIsMobileMenuOpen(false);
              }}
            >
              СВЯЗЬ С КОМАНДОЙ
            </li>
          </ul>
        </div>
      </div>

      {/* ========== ГЛАВНОЕ СОДЕРЖИМОЕ ========== */}
      <main className="main-content">
        <div className="container">
          {/* Заголовок */}
          <h1 className="main-title">Scuderia Ferrari</h1>

          {/* Основной текст */}
          <div className="main-text">
            <p className="text-line">ФЕРРАРИ – ЭТО БОЛЬШЕ, ЧЕМ ПРОСТО ГОНКИ,</p>
            <p className="text-line">ЭТО ИТАЛЬЯНСКАЯ ЛЕГЕНДА НА КОЛЕСАХ.</p>
          </div>

          {/* Кнопка связи с командой */}
          <button
            ref={buttonRef}
            className="contact-button"
            onClick={() => setIsModalOpen(true)}
          >
            Связь с командой
          </button>

          {/* Декоративные элементы Vantage */}
          <div className="vantage-elements">
            <div className="vantage-text">V vantage</div>
            <div className="vantage-text">V vantage</div>
          </div>
        </div>
      </main>

      {/* ========== МОДАЛЬНОЕ ОКНО ФОРМЫ ========== */}
      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div
            ref={modalRef}
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
          >
            <button className="close-modal" onClick={closeModal}>
              ×
            </button>

            <h2 className="form-title">СВЯЗЬ С КОМАНДОЙ</h2>

            <form onSubmit={handleSubmit} className="contact-form">
              {/* Поле имени */}
              <div className="form-group">
                <label htmlFor="name">Ваше имя *</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  required
                  disabled={isSubmitting}
                  placeholder="Введите ваше имя"
                />
              </div>

              {/* Поле email */}
              <div className="form-group">
                <label htmlFor="email">Email *</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                  disabled={isSubmitting}
                  placeholder="example@mail.com"
                />
              </div>

              {/* Поле телефона */}
              <div className="form-group">
                <label htmlFor="phone">Телефон</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  placeholder="+7 (___) ___-__-__"
                />
              </div>

              {/* Поле сообщения */}
              <div className="form-group">
                <label htmlFor="message">Сообщение *</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                  required
                  disabled={isSubmitting}
                  placeholder="Напишите ваше сообщение..."
                  rows="4"
                />
              </div>

              {/* Сообщение об отправке */}
              {submitMessage && (
                <div className={`submit-message ${submitStatus}`}>
                  {submitMessage}
                </div>
              )}

              {/* Кнопка отправки */}
              <button
                type="submit"
                className="submit-button"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <span className="spinner"></span>
                    Отправка...
                  </>
                ) : (
                  "Отправить"
                )}
              </button>
            </form>
          </div>
        </div>
      )}

      {/* ========== ОКНО ПАРТНЕРОВ ========== */}
      <div className={`partners-overlay ${isPartnersOpen ? "open" : ""}`}>
        <div className="partners-content">
          <button className="close-partners" onClick={togglePartners}>
            ×
          </button>

          <h2 className="partners-title">ПАРТНЕРЫ</h2>

          <div className="partners-grid">
            {/* Ключевые фигуры */}
            <div className="partners-section">
              <h3>Ключевые фигуры команды Феррари</h3>
              <div className="figures-list">
                <div className="figure-item">Глава команды</div>
                <div className="figure-item">Технический директор</div>
                <div className="figure-item">Пилот 1</div>
                <div className="figure-item">Пилот 2</div>
              </div>
            </div>

            {/* Спонсоры */}
            <div className="partners-section">
              <h3>Спонсоры</h3>
              <div className="sponsors-list">
                <div className="sponsor-item">VANTAGE</div>
                <div className="sponsor-item">Shell</div>
                <div className="sponsor-item">Ray-Ban</div>
                <div className="sponsor-item">UPS</div>
              </div>
            </div>

            {/* Титульные спонсоры */}
            <div className="partners-section">
              <h3>Титульные спонсоры</h3>
              <div className="title-sponsors">
                <div className="title-sponsor">VANTAGE</div>
                <div className="title-sponsor">AMERICAN EXPRESS</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* ========== ФУТЕР ========== */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-links">
              <a href="#" className="footer-link">
                ВАША ЦЕЛЬ
              </a>
              <a href="#" className="footer-link">
                ВАШИ ВОЗМОЖНОСТИ
              </a>
              <a href="#" className="footer-link">
                ВАША ИСТОРИЯ
              </a>
              <a href="#" className="footer-link">
                ВАШИ СООБЩЕНИЯ
              </a>
            </div>

            <button
              className="footer-contact-btn"
              onClick={() => setIsModalOpen(true)}
            >
              СВЯЖИТЕСЬ С НАМИ
            </button>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default HeadScuderri;
