import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './DriverCardDeskription';
import { Button } from './Button';
import '../components.css';

const drivers = [
  {
    id: 1,
    name: "Lewis Hamilton",
    nameRu: "ЛЬЮИС ХЭМИЛТОН",
    image: "/lewis-hamilton-ferrari-racing-suit.jpg",
    number: 44,
    nationality: "Великобритания",
    bio: "7-кратный чемпион мира Формулы-1. Присоединился к Ferrari в 2025 году, принеся с собой огромный опыт и стремление к новым победам.",
  },
  {
    id: 2,
    name: "Charles Leclerc",
    nameRu: "ШАРЛЬ ЛЕКЛЕР",
    image: "/charles-leclerc-ferrari-sunglasses-helmet.jpg",
    number: 16,
    nationality: "Монако",
    bio: "Молодой талантливый пилот, являющийся лицом новой эры Ferrari. Известен своей агрессивной манерой вождения и способностью выжимать максимум из машины.",
  },
  {
    id: 3,
    name: "Zhou Guanyu",
    nameRu: "ЧЖОУ ГУАНЬЮЙ",
    image: "/zhou-guanyu-ferrari-cap-racing-suit.jpg",
    number: 24,
    nationality: "Китай",
    bio: "Первый китайский пилот в истории Ferrari. Приносит свежий взгляд и представляет команду на быстрорастущем азиатском рынке.",
  },
];

export default function PilotsPage() {
  const [selectedDriver, setSelectedDriver] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  const handleDriverClick = (driver) => {
    setSelectedDriver(driver);
    setIsDialogOpen(true);
  };

  return (
    <main className="pilots-page">
      {/* Background number pattern on the right */}
      <div className="number-pattern">
        {[...Array(12)].map((_, i) => (
          <div key={i} className="pattern-number">
            {i % 2 === 0 ? "444" : "161"}
          </div>
        ))}
      </div>

      {/* Main content */}
      <div className="pilots-container">
        {/* Title */}
        <h1 className="pilots-title">
          ПИЛОТЫ
        </h1>

        {/* Driver cards grid */}
        <div className="drivers-grid">
          {drivers.map((driver, index) => (
            <div key={driver.id} className={`driver-card ${index === 1 ? "middle-driver" : ""}`}>
              {/* Driver image card */}
              <div className="driver-image-container">
                <div className="driver-image-wrapper">
                  <img
                    src={driver.image || "/placeholder.svg"}
                    alt={driver.name}
                    className="driver-image"
                  />
                </div>
              </div>

              {/* Driver name */}
              <h2 className="driver-name">
                {driver.nameRu}
              </h2>

              {/* More details button */}
              <Button
                onClick={() => handleDriverClick(driver)}
                className="driver-button"
              >
                ПОДРОБНЕЕ
              </Button>
            </div>
          ))}
        </div>
      </div>

      {/* Driver info modal */}
      <Dialog 
        open={isDialogOpen} 
        onOpenChange={setIsDialogOpen}
        className="driver-dialog"
      >
        <DialogHeader>
          <DialogTitle>{selectedDriver?.nameRu}</DialogTitle>
        </DialogHeader>
        <DialogContent>
          {selectedDriver && (
            <div className="driver-modal-content">
              <div className="driver-modal-image">
                <img
                  src={selectedDriver.image || "/placeholder.svg"}
                  alt={selectedDriver.name}
                />
              </div>
              <div className="driver-info">
                <p>
                  <strong>Номер:</strong> {selectedDriver.number}
                </p>
                <p>
                  <strong>Национальность:</strong> {selectedDriver.nationality}
                </p>
                <p>{selectedDriver.bio}</p>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </main>
  );
}
