import React, { useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, Button } from './Dialog';
import '../components.css';

const DriversCarousel = ({ drivers }) => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  // Определяем индексы для отображения
  const getDriverIndex = (position) => {
    const total = drivers.length;
    let index = currentIndex + position;

    if (index < 0) index = total - 1;
    if (index >= total) index = 0;

    return index % total;
  };

  // Пилоты для отображения: слева, центр, справа
  const leftDriver = drivers[getDriverIndex(-1)];
  const centerDriver = drivers[getDriverIndex(0)];
  const rightDriver = drivers[getDriverIndex(1)];

  const handlePrev = () => {
    setCurrentIndex((prev) => (prev === 0 ? drivers.length - 1 : prev - 1));
  };

  const handleNext = () => {
    setCurrentIndex((prev) => (prev === drivers.length - 1 ? 0 : prev + 1));
  };

  const handleDriverClick = (indexOffset) => {
    setCurrentIndex((prev) => getDriverIndex(indexOffset));
  };

  const handleOpenDialog = () => {
    setIsDialogOpen(true);
  };

  const getDriverNumber = (name) => {
    if (name.includes("Хэмилтон")) return "44";
    if (name.includes("Леклер")) return "16";
    return "24";
  };

  return (
    <section className="drivers-carousel-section">
      <h2 className="section-title">ПИЛОТЫ SCUDERIA FERRARI</h2>

      <div className="carousel-container">
        {/* Левое фото (меньше) */}
        <div 
          className="driver-photo-wrapper left-photo"
          onClick={() => handleDriverClick(-1)}
        >
          <div className="photo-container">
            <img 
              src={leftDriver.photo || "/placeholder-driver.jpg"} 
              alt={leftDriver.name}
              className="driver-photo"
            />
            <div className="photo-overlay">
              <span className="driver-name-small">{leftDriver.name.split(' ')[1]}</span>
              <span className="driver-number">{getDriverNumber(leftDriver.name)}</span>
            </div>
          </div>
        </div>

        {/* Центральное фото (больше) */}
        <div className="center-driver">
          <div 
            className="driver-photo-wrapper center-photo"
            onClick={handleOpenDialog}
          >
            <div className="photo-container">
              <img 
                src={centerDriver.photo || "/placeholder-driver.jpg"} 
                alt={centerDriver.name}
                className="driver-photo"
              />
              <div className="photo-overlay">
                <span className="driver-name">{centerDriver.name}</span>
                <span className="driver-number-large">{getDriverNumber(centerDriver.name)}</span>
              </div>
            </div>
          </div>

          {/* Кнопка подробнее под центральным фото */}
          <Button 
            onClick={handleOpenDialog}
            className="center-driver-button"
          >
            ПОДРОБНЕЕ
          </Button>

          {/* Навигационные стрелки */}
          <div className="carousel-controls">
            <button className="nav-button prev-button" onClick={handlePrev}>
              <svg width="24" height="24" viewBox="0 0 24 24">
                <path d="M15 18l-6-6 6-6" fill="none" stroke="currentColor" strokeWidth="2" />
              </svg>
            </button>
            <button className="nav-button next-button" onClick={handleNext}>
              <svg width="24" height="24" viewBox="0 0 24 24">
                <path d="M9 18l6-6-6-6" fill="none" stroke="currentColor" strokeWidth="2" />
              </svg>
            </button>
          </div>
        </div>

        {/* Правое фото (меньше) */}
        <div 
          className="driver-photo-wrapper right-photo"
          onClick={() => handleDriverClick(1)}
        >
          <div className="photo-container">
            <img 
              src={rightDriver.photo || "/placeholder-driver.jpg"} 
              alt={rightDriver.name}
              className="driver-photo"
            />
            <div className="photo-overlay">
              <span className="driver-name-small">{rightDriver.name.split(' ')[1]}</span>
              <span className="driver-number">{getDriverNumber(rightDriver.name)}</span>
            </div>
          </div>
        </div>
      </div>

      {/* Индикатор текущего пилота */}
      <div className="carousel-indicator">
        {drivers.map((_, index) => (
          <button
            key={index}
            className={`indicator-dot ${index === currentIndex ? 'active' : ''}`}
            onClick={() => setCurrentIndex(index)}
          />
        ))}
      </div>

      {/* Диалог с информацией о пилоте */}
      <Dialog 
        open={isDialogOpen} 
        onOpenChange={setIsDialogOpen}
        className="driver-dialog"
      >
        <DialogHeader>
          <DialogTitle>{centerDriver.name}</DialogTitle>
        </DialogHeader>
        <DialogContent>
          <div className="driver-dialog-content">
            <div className="dialog-header">
              <div className="dialog-photo-container">
                <img 
                  src={centerDriver.photo || "/placeholder-driver.jpg"} 
                  alt={centerDriver.name}
                  className="dialog-photo"
                />
                <div className="dialog-photo-info">
                  <span className="dialog-number">{getDriverNumber(centerDriver.name)}</span>
                  <span className="dialog-team">Scuderia Ferrari</span>
                </div>
              </div>

              <div className="dialog-quote">
                <p className="quote-text">"{centerDriver.quote}"</p>
              </div>
            </div>

            <div className="dialog-bio">
              <h4>БИОГРАФИЯ</h4>
              <p>{centerDriver.bio}</p>
            </div>

            <div className="dialog-stats">
              <div className="stat-item">
                <span className="stat-label">Дебют в F1</span>
                <span className="stat-value">
                  {centerDriver.name.includes("Хэмилтон") ? "2007" : 
                   centerDriver.name.includes("Леклер") ? "2018" : "2022"}
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Чемпионства</span>
                <span className="stat-value">
                  {centerDriver.name.includes("Хэмилтон") ? "7" : 
                   centerDriver.name.includes("Леклер") ? "0" : "0"}
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Победы</span>
                <span className="stat-value">
                  {centerDriver.name.includes("Хэмилтон") ? "103" : 
                   centerDriver.name.includes("Леклер") ? "5" : "0"}
                </span>
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </section>
  );
};

export default DriversCarousel;
