import React, { useState } from 'react';
import './components5.css';

const Tracci = () => {
  const [showAll, setShowAll] = useState(false);

  // Все трассы
  const allTracks = [
    { id: 1, name: "AUSTRALIAN GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 2, name: "CHINESE GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 3, name: "JAPANESE GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 4, name: "BAHRAIN GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 5, name: "SAUDI ARABIAN GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 6, name: "SPANISH GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 7, name: "MONACO GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 8, name: "CANADIAN GRAND PRIX", driver: "MONO DOTTALE >" },
    { id: 9, name: "BRITISH GRAND PRIX", driver: "MONO DOTTALE >" },
  ];

  // Показываем 5 в закрытом виде, все в открытом
  const tracks = showAll ? allTracks : allTracks.slice(0, 5);

  return (
    <div className="tracci-container">
      <h1 className="tracci-title">TPacCbl</h1>

      <div className="tracks-grid">
        {tracks.map(track => (
          <div key={track.id} className="track-card">
            <div className="track-square">
              <div className="track-image-placeholder"></div>
              <div className="track-text">
                <h3 className="track-name">{track.name}</h3>
                <p className="track-driver">{track.driver}</p>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="button-container">
        {!showAll ? (
          <button 
            className="tracci-button"
            onClick={() => setShowAll(true)}
          >
            показать все трассы
          </button>
        ) : (
          <button 
            className="tracci-button"
            onClick={() => setShowAll(false)}
          >
            свернуть
          </button>
        )}
      </div>
    </div>
  );
};

export default Tracci;
